#include <stdio.h>#include <iostream>#include <algorithm>using namespace std;#define ll __int64const int N = 500010;int a[N],b[N];int main(){	int L,n,m,i,l,r,t,mid,ans;	ll jump;	while(scanf("%d%d%d",&L,&n,&m) != EOF)	{		for(i=0 ; i < n ; ++i)			scanf("%d",&a[i]);		sort(a,a+n);		b[0] = a[0];		for(i=1 ; i < n ; ++i)			b[i] = a[i]-a[i-1];		b[n] = L-a[n-1];		l=0; r=L; ans = L;		while(l <= r){			jump=0;			t = 1;			bool f = true;			mid = (l+r)>>1;			for(i=0 ; i <= n ; ++i){				if(b[i] > mid){					f = false; break;				}				if(jump+b[i] <= mid) jump += b[i];				else {					jump = b[i];					t++;				}			}			if(f && t <= m){				ans = mid;				r = mid-1;			} else l = mid+1;		}		printf("%d\n",ans);	}	return 0;}